func PPPKPassingGrade(cat string) float32 {
	switch strings.ToLower(cat) {
	case "manajerial":
		return 65
	case "sosial_kultural":
		return 52
	case "teknis_kementerian_pkb":
		return 220
	case "teknis_kementerian_arsiparis":
		return 225
	case "teknis_guru_pedagogik", "teknis_guru_tendik", "teknis_guru_mapel_kelas":
		return 180
	case "teknis_kesehatan_keperawatan", "teknis_kesehatan_bidan",
		"teknis_kesehatan_medis", "teknis_kesehatan_agizi", "teknis_kesehatan_teka",
		"teknis_kesehatan_masyarakat", "teknis_kesehatan_dokter":
		return 158
	default:
		return 158
	}
}
